import {logCommand} from 'execa/lib/verbose/start.js';
import {getVerboseInfo} from 'execa/lib/verbose/info.js';
import {getStartTime} from 'execa/lib/return/duration.js';
import {joinCommand} from 'execa/lib/arguments/escape.js';
import {normalizeFdSpecificOption} from 'execa/lib/arguments/specific.js';

// Compute `result.command`, `result.escapedCommand` and `verbose`-related information
export const handleCommand = (filePath, rawArguments, rawOptions) => {
	const startTime = getStartTime();
	const {command, escapedCommand} = joinCommand(filePath, rawArguments);
	const verbose = normalizeFdSpecificOption(rawOptions, 'verbose');
	const verboseInfo = getVerboseInfo(verbose, escapedCommand, {...rawOptions});
	logCommand(escapedCommand, verboseInfo);
	return {
		command,
		escapedCommand,
		startTime,
		verboseInfo,
	};
};
